import{_ as e,M as r,aw as a,L as o}from"./chunks/framework.CzXSTpVB.js";const p=JSON.parse('{"title":"关于 a4v","description":"","frontmatter":{},"headers":[],"relativePath":"guide/introduction/a4v.md","filePath":"guide/introduction/a4v.md","lastUpdated":1727685155000}'),s={name:"guide/introduction/a4v.md"};function n(i,t,l,g,c,d){return o(),r("div",null,t[0]||(t[0]=[a('<h1 id="关于-a4v" tabindex="-1">关于 a4v <a class="header-anchor" href="#关于-a4v" aria-label="Permalink to &quot;关于 a4v&quot;">​</a></h1><p><a href="https://github.com/Mikasa33/a4v" target="_blank" rel="noreferrer">a4v</a> 是一个基于 <a href="https://cn.vuejs.org/" target="_blank" rel="noreferrer">Vue3.0</a>、<a href="https://cn.vitejs.dev/" target="_blank" rel="noreferrer">Vite</a>、 <a href="https://www.typescriptlang.org/zh/" target="_blank" rel="noreferrer">TypeScript</a>、 <a href="https://www.naiveui.com/zh-CN/light/" target="_blank" rel="noreferrer">Naive UI</a> 的中后台解决方案，目标是为开发中大型项目提供开箱即用的解决方案。包括二次封装组件、utils、hooks、动态菜单、权限校验、多主题配置、按钮级别权限控制等功能。项目会使用前端较新的技术栈，可以作为项目的启动模板，以帮助你快速搭建中后台产品原型。也可以作为一个示例，用于学习 <code>vue3</code>、<code>vite</code>、<code>ts</code> 等主流技术。该项目会持续跟进最新技术，并将其应用在项目中。</p><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><ul><li><strong>最新技术栈</strong>：使用 <code>Vue3</code>、<code>Vite</code>、<code>TypeScript</code> 等前端前沿技术开发。</li><li><strong>国际化</strong>：内置完善的国际化方案，支持多语言切换。</li><li><strong>权限验证</strong>：完善的权限验证方案，按钮级别权限控制。</li><li><strong>多主题</strong>：内置多种主题配置和黑暗模式，满足个性化需求。</li><li><strong>动态菜单</strong>：支持动态菜单，可以根据权限配置显示菜单。</li><li><strong>Mock 数据</strong>：基于 <code>Nitro</code> 的本地高性能 Mock 数据方案。</li><li><strong>组件丰富</strong>：提供了丰富的组件，可以满足大部分的业务需求。</li><li><strong>规范</strong>：代码规范，使用 <code>ESLint</code>、<code>Stylelint</code> 等工具保证代码质量。</li><li><strong>工程化</strong>：使用 <code>Pnpm Monorepo</code> 等工具，提高开发效率。</li><li><strong>UI 库支持</strong>：支持 <code>Naive UI</code> 组件库，提供丰富的 UI 组件。</li></ul><h2 id="浏览器支持" tabindex="-1">浏览器支持 <a class="header-anchor" href="#浏览器支持" aria-label="Permalink to &quot;浏览器支持&quot;">​</a></h2><p><strong>本地开发</strong>推荐使用 <code>Chrome 最新版</code> 浏览器，<strong>不支持</strong> <code>Chrome 80</code> 以下版本。</p><p><strong>生产环境</strong>支持现代浏览器，不支持 <code>IE</code>。</p><table tabindex="0"><thead><tr><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/archive/internet-explorer_9-11/internet-explorer_9-11_48x48.png" alt="IE" width="24px" height="24px"></a>IE</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/edge/edge_48x48.png" alt=" Edge" width="24px" height="24px"></a>Edge</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/firefox/firefox_48x48.png" alt="Firefox" width="24px" height="24px"></a>Firefox</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/chrome/chrome_48x48.png" alt="Chrome" width="24px" height="24px"></a>Chrome</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/safari/safari_48x48.png" alt="Safari" width="24px" height="24px"></a>Safari</th></tr></thead><tbody><tr><td style="text-align:center;">不支持</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td></tr></tbody></table>',8)]))}const b=e